-- ----------------------------
--  Function structure for public._postgis_deprecate(text, text, text)
-- ----------------------------
DROP FUNCTION IF EXISTS "public"."_postgis_deprecate"(text, text, text);
CREATE FUNCTION "public"."_postgis_deprecate"(IN oldname text, IN newname text, IN "version" text) RETURNS "void" 
	AS $BODY$
DECLARE
  curver_text text;
BEGIN
  --
  -- Raises a NOTICE if it was deprecated in this version,
  -- a WARNING if in a previous version (only up to minor version checked)
  --
    curver_text := '2.1.1';
    IF split_part(curver_text,'.',1)::int > split_part(version,'.',1)::int OR
       ( split_part(curver_text,'.',1) = split_part(version,'.',1) AND
         split_part(curver_text,'.',2) != split_part(version,'.',2) )
    THEN
      RAISE WARNING '% signature was deprecated in %. Please use %', oldname, version, newname;
    ELSE
      RAISE DEBUG '% signature was deprecated in %. Please use %', oldname, version, newname;
    END IF;
END;
$BODY$
	LANGUAGE plpgsql
	COST 100
	STRICT
	SECURITY INVOKER
	IMMUTABLE;
ALTER FUNCTION "public"."_postgis_deprecate"(IN oldname text, IN newname text, IN "version" text) OWNER TO "postgres";

