-- ----------------------------
DROP FUNCTION IF EXISTS "public"."_contract_on"(text);
CREATE FUNCTION "public"."_contract_on"(IN text) RETURNS "char" 
	AS $BODY$
   SELECT CASE substring(LOWER($1) FROM 1 FOR 1)
          WHEN 's' THEN '1'::"char"
          WHEN 'u' THEN '2'::"char"
          WHEN 'i' THEN '3'::"char"
          WHEN 'd' THEN '4'::"char"
          ELSE          '0'::"char" END
$BODY$
