-- ----------------------------
DROP FUNCTION IF EXISTS "public"."results_eq"(refcursor, refcursor, text);
CREATE FUNCTION "public"."results_eq"(IN refcursor, IN refcursor, IN text) RETURNS "text" 
	AS $BODY$
DECLARE
    have       ALIAS FOR $1;
    want       ALIAS FOR $2;
    have_rec   RECORD;
    want_rec   RECORD;
    have_found BOOLEAN;
    want_found BOOLEAN;
    rownum     INTEGER := 1;
BEGIN
    FETCH have INTO have_rec;
    have_found := FOUND;
    FETCH want INTO want_rec;
